import { FC } from 'react';
import { Table } from 'antd';
import data from '../../mock_data';
import { Link } from 'react-router-dom';
import Breadcrumb from '../Breadcrumb';

type dataItem = {
    key: number,
    user_id: number,
    question_id: number,
    by: string,
    title: string,
    message: string,
    submission_time: string,
    view: number,
    vote: number
}

const columns = [
  {
    title: 'Title',
    dataIndex: 'title',
    sorter: {
        compare: (a: dataItem, b: dataItem) => {
            let x = a.title.toLowerCase();
            let y = b.title.toLowerCase();
            if (x < y) {return -1;}
            if (x > y) {return 1;}
            return 0;
        },
        multiple: 3,
    },
    render: (title: string, record: dataItem) => <Link to={`/question/${record.question_id}`}>{title}</Link>,
    width: '550px',
  },
  {
    title: 'Message',
    dataIndex: 'message',
    render: (msg: string) => `${msg.substring(0, 500)}...`
  },
  {
    title: 'Submission Time',
    dataIndex: 'submission_time',
    sorter: {
        compare: (a: dataItem, b: dataItem) => {
            let x = a.submission_time.toLowerCase();
            let y = b.submission_time.toLowerCase();
            if (x < y) {return -1;}
            if (x > y) {return 1;}
            return 0;
        }
    },
    width: '215px'
  },
  {
    title: 'View',
    dataIndex: 'view',
    sorter: {
        compare: (a: dataItem, b: dataItem) => a.view - b.view
    },
  },
  {
    title: 'Vote',
    dataIndex: 'vote',
    sorter: {
        compare: (a: dataItem, b: dataItem) => a.vote - b.vote
    },
  },
];

function onChange(pagination: any, filters: any, sorter: any, extra: any) {
  console.log('params', pagination, filters, sorter, extra);
}

const AllQuestions: FC = () => (
    <>
      <Breadcrumb locationArray={['All Questions']} />
      <div className="site-layout-content">
        <Table 
            columns={columns}
            dataSource={data}
            onChange={onChange}
            bordered
            pagination= {{ 
                position: ['bottomCenter'],
                pageSize: 1
            }} />
      </div>
    </>
);

export default AllQuestions;
