import { Comment, Avatar } from 'antd';
import moment from 'moment';
import React from 'react';
import AnswerList from './AnswerList';
import Editor from './Editor';
import { AnswerInterface } from '../../interfaces/Answer';

class Answers extends React.Component<{question_id: string}> {
  constructor(props: {question_id: string}) {
    super(props);
  }

  state = {
    answers: [],
    submitting: false,
    value: '',
  };

  fetchAnswers = async () => {
      const res = await fetch(`http://localhost:5000/answers`);
      const data = await res.json();
      return data;
  }

  componentDidMount() {
      const getAnswers = async () => {
        const answers = await this.fetchAnswers();
        const filteredAnswers = answers.filter((answer: AnswerInterface) => answer.question_id === parseInt(this.props.question_id));
        this.setState({ ...filteredAnswers });
      }
      getAnswers();
  }

  handleSubmit = () => {
    if (!this.state.value) {
      return;
    }

    this.setState({
      submitting: true,
    });

    setTimeout(() => {
      this.setState({
        submitting: false,
        value: '',
        answers: [
          ...this.state.answers,
          {
            author: 'Han Solo',
            avatar: 'https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png',
            content: <p>{this.state.value}</p>,
            datetime: moment().fromNow(),
          },
        ],
      });
    }, 1000);
  };

  handleChange = (e: { target: { value: string; }; }) => {
    this.setState({
      value: e.target.value,
    });
  };

  render() {
    const { answers, submitting, value } = this.state;

    return (
      <div className="comment-section">
        <h2>Answers</h2>
        {answers.length > 0 && <AnswerList answers={answers} />}
        <Comment
          avatar={
            <Avatar
              src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png"
              alt="Han Solo"
            />
          }
          content={
            <Editor
              onChange={this.handleChange}
              onSubmit={this.handleSubmit}
              submitting={submitting}
              value={value}
            />
          }
        />
      </div>
    );
  }
}

export default Answers;
