import { Comment, Avatar } from 'antd';
import React from 'react';
import AnswerList from './AnswerList';
import Editor from './Editor';
import { AnswerInterface } from '../../interfaces/AnswerInterface';
import { NewAnswerInterface } from '../../interfaces/NewAnswerInterface';
import { gql, useMutation } from '@apollo/client'

const POST_ANSWER_MUTATION = gql`
mutation postAnswerMutation(
  $questionId: ID!
  $content: String!
  $avatar: String!
){
  postAnswer(
    questionId: $questionId,
    content: $content,
    avatar: $avatar) {
      id
    }
}
`

const [ postAnswer ] = useMutation(POST_ANSWER_MUTATION, {
  onCompleted: () => console.log('done')
})
class Answers extends React.Component<{answers: AnswerInterface[], answersRefetch: Function}> {
  state = {
    answers: [],
    submitting: false,
    value: '',
  };

  deleteAnswer = async (answerId: number) => {
    await fetch(`http://localhost:5000/answers/${answerId}`, {method: 'DELETE'});
    this.setState({
      submitting: false,
      value: '',
      answers: [...this.state.answers.filter((answer: AnswerInterface) => answer.id !== answerId)],
    });
  }

  componentDidMount() {
    this.setState({
      ...this.state,
      answers: [ ...this.props.answers ]
    })
  }


  handleSubmit = () => {
    if (!this.state.value) {
      return;
    }

    this.setState({
      submitting: true,
    });

    setTimeout(() => {
      this.addAnswer({
        author: 'Pisti',
        user_id: 1,
        question_id: parseInt(this.props.question_id),
        avatar: 'https://i.pravatar.cc/400?img=33',
        content: this.state.value,
        datetime: Date(),
      })
    }, 1000);
  };

  handleChange = (e: { target: { value: string; }; }) => {
    this.setState({
      value: e.target.value,
    });
  };

  render() {
    const { answers, submitting, value } = this.state;

    return (
      <div className="comment-section">
        <h2>Answers</h2>
        {answers.length > 0 && <AnswerList 
          answers={answers}
          deleteHandler={this.deleteAnswer} />}
        <Comment
          avatar={
            <Avatar
              src="https://i.pravatar.cc/400?img=33"
              alt="Han Solo"
            />
          }
          content={
            <Editor
              onChange={this.handleChange}
              onSubmit={this.handleSubmit}
              submitting={submitting}
              value={value}
            />
          }
        />
      </div>
    );
  }
}

export default Answers;
