import { FC, useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Breadcrumb from '../Breadcrumb';
import { Card, Row, Col } from 'antd';
import { DataItem } from '../../interfaces/DataItem';
import data from '../../mock_data';

const Question: FC = () => {
    const {id}: {id: string} = useParams();
    const [question, setQuestion] = useState<DataItem>();

    const loadData = (id: string) => {
        return data.filter((question) => question.question_id === parseInt(id))[0];
    }

    useEffect(() => {
        const question = loadData(id);
        setQuestion(question);
    }, [id]);

    return (
        <>
            <Breadcrumb locationArray={['Home', 'Question', 'How to set a custom ringtone on your Nokia 3310?']} />
            <div className="site-layout-content">  
                {question && <Card title={question.title} style={{width: '60%', margin: '0 auto'}}>
                    <Row gutter={16} style={{marginBottom: '24px'}}>
                        <Col span={6}>
                            <Card size="small" title="Submitted at" bordered={true} extra={<a href="http://google.com">more</a>}>
                            Card content
                            </Card>
                        </Col>
                        <Col span={6}>
                            <Card size="small" title="Author" bordered={true}>
                            Card content
                            </Card>
                        </Col>
                        <Col span={6}>
                            <Card size="small" title="View" bordered={true}>
                            Card content
                            </Card>
                        </Col>
                        <Col span={6}>
                            <Card size="small" title="Vote" bordered={true}>
                            Card content
                            </Card>
                        </Col>
                    </Row>
                    <Card>
                        {question.message}
                    </Card>
                    <h2>Answers</h2>
                </Card>}
            </div>
        </>
    )
}

export default Question;
