import { FC, useState } from 'react';
import { Redirect, useParams } from 'react-router-dom';
import Breadcrumb from '../Breadcrumb';
import Answers from '../Answers/Answers';
import { Card, Row, Col, Modal } from 'antd';
import { CaretUpOutlined, CaretDownOutlined, ExclamationCircleOutlined } from '@ant-design/icons';
import { QuestionInterface } from '../../interfaces/QuestionInterface';
import IconBar from '../IconBar';
import EditQuestion from '../EditQuestion';
import { gql, useQuery, useMutation } from '@apollo/client'

const { confirm } = Modal;

const FEED_QUESTION_QUERY = gql`
query feedQuestionQuery($id: ID!) {
    feedQuestion(id: $id) {
        title
        message
        submissionTime
        view
        voteCount
    }
}
`

const UPDATE_QUESTION_MUTATION = gql`
query feedQuestionQuery(
    $id: ID!
    $title: String!
    $message: String!) {
    feedQuestion(id: $id, title: $title, message: $message) {
        id
    }
}
`

const Question: FC = () => {
    const {id}: {id: string} = useParams();
    const [question, setQuestion] = useState<QuestionInterface>();
    const [redirect, setRedirect] = useState("");
    const [edit, setEdit] = useState(false);

    useQuery(FEED_QUESTION_QUERY, {
        variables: { id: parseInt(id) },
        onCompleted: (response) => setQuestion(response.feedQuestion)
    })

    //const [updateQuestion] = useMutation(UPDATE_QUESTION_MUTATION)

    const editHandler = (values: any) => {
        // updateQuestion({ variables: {
        //     id: id,
        //     title: values.title,
        //     message: values.message
        // }})
        setEdit(false);
    }

    const deleteHandler = async () => {
        await fetch(`http://localhost:5000/questions/${id}`,
            {method: 'DELETE'});
        setRedirect("/");
    }

    function showDeleteConfirm() {
        confirm({
          title: 'Delete this question?',
          icon: <ExclamationCircleOutlined />,
          content: 'Are you sure you want to delete this question?',
          okText: 'Yes',
          okType: 'danger',
          cancelText: 'No',
          onOk() {
            deleteHandler();
          },
          onCancel() {
            console.log('Cancel');
          },
        });
      }

    return (
        <>
            {redirect && <Redirect to={redirect} />}
            <Breadcrumb locationArray={['Home', 'Question', question ? question.title : '']} />
            <div className="site-layout-content">  
                {question && <Card 
                                title={question.title}
                                className="question-card"
                                extra={<IconBar 
                                            className="question-icon-bar"
                                            editHandler={() => setEdit(true)}
                                            deleteHandler={showDeleteConfirm} />}>
                    {edit && <EditQuestion question={question} editHandler={editHandler} />}
                    <Row gutter={16} className="details">
                        <Col span={6}>
                            <Card size="small" title="Submitted at" bordered={true}>
                            {question.submissionTime}
                            </Card>
                        </Col>
                        <Col span={6}>
                            <Card size="small" title="Author" bordered={true}>
                            {question.user_name}
                            </Card>
                        </Col>
                        <Col span={6}>
                            <Card size="small" title="View" bordered={true}>
                            {question.view}
                            </Card>
                        </Col>
                        <Col span={6}>
                            <Card 
                                size="small"
                                title="Vote"
                                bordered={true}
                                extra={<><a href="http://google.com">UP<CaretUpOutlined /></a><a href="http://google.com">DOWN<CaretDownOutlined /></a></>}>
                            {question.voteCount}
                            </Card>
                        </Col>
                    </Row>
                    <Card className="question-message">
                        <div dangerouslySetInnerHTML={{__html: question.message.replace(/(\r\n|\n\r|\r|\n)/g, '<br>')}}></div>
                    </Card>
                    {/* <Answers question_id={id} /> */}
                </Card>}
            </div>
        </>
    )
}

export default Question;
