import { FC, useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Breadcrumb from '../Breadcrumb';
import { Card } from 'antd';
import { DataItem } from '../../interfaces/DataItem';
import data from '../../mock_data';

const Question: FC = () => {
    const {id}: {id: string} = useParams();
    const [question, setQuestion] = useState<DataItem>();

    const loadData = () => {
        return data.filter((question) => question.key === parseInt(id))[0];
    }

    useEffect(() => {
        const getData = () => {
            const currentQuestion = loadData();
            setQuestion(currentQuestion);
        }

        getData();
    }, []);

    return (
        <>
            <Breadcrumb locationArray={['Home', 'Question', 'How to set a custom ringtone on your Nokia 3310?']} />
            <div className="site-layout-content">  
                {question && <Card title={question.title}>
                    <Card type="inner" title="Inner Card title" extra={<a href="#">More</a>}>
                    Inner Card content
                    </Card>
                    <Card
                    style={{ marginTop: 16 }}
                    type="inner"
                    title="Inner Card title"
                    extra={<a href="#">More</a>}
                    >
                    Inner Card content
                    </Card>
                </Card>}
            </div>
        </>
    )
}

export default Question;
