import { FC, useState } from 'react';
import Breadcrumb from '../Breadcrumb';
import Result from '../Result';
import { Form, Input, InputNumber, Button, Card } from 'antd';

const layout = {
  labelCol: { span: 8 },
  wrapperCol: { span: 16 },
};

/* eslint-disable no-template-curly-in-string */
const validateMessages = {
  required: '${label} is required!',
  types: {
    email: '${label} is not a valid email!',
    number: '${label} is not a valid number!',
  },
  number: {
    range: '${label} must be between ${min} and ${max}',
  },
};
/* eslint-enable no-template-curly-in-string */

const Registration: FC = () => {
    const [validData, setValidData] = useState();
    const [form] = Form.useForm();

    const validatePassword = (rule: any, value: string, callback: Function) => {
        form.get
        // if (value && value !== "Secret") {
        //     callback("Error!");
        // } else {
        //     callback();
        // }
    };

    const onFinish = (values: any) => {
        console.log(values);
        setValidData(values);
        form.resetFields();
    };

    return (
        <>
            <Breadcrumb locationArray={['Home', 'New User Registration']} />
            <div className="site-layout-content">
            <Card className="registration-form-card">
                {!validData ? <Form {...layout} name="nest-messages" form={form} onFinish={onFinish} validateMessages={validateMessages}>
                        <Form.Item name={['user', 'name']} label="Name" rules={[{ required: true }]}>
                            <Input />
                        </Form.Item>
                        <Form.Item name={['user', 'email']} label="Email" rules={[{ type: 'email' , required: true}]}>
                            <Input />
                        </Form.Item>
                        <Form.Item name={['user', 'password']} label="Password" rules={[{ required: true}]} hasFeedback>
                            <Input type="password" placeholder="Password" />
                        </Form.Item>
                        <Form.Item
                            name="confirm"
                            label="Confirm Password"
                            dependencies={['password']}
                            hasFeedback
                            rules={[
                            {
                                required: true,
                                message: 'Please confirm your password!',
                            },
                            ({ getFieldValue }) => ({
                                validator(_, value) {
                                if (!value || getFieldValue('password') === value) {
                                    return Promise.resolve();
                                }
                                return Promise.reject(new Error('The two passwords that you entered do not match!'));
                                },
                            }),
                            ]}
                        >
                            <Input type="password" placeholder="Confirm Password" />
                        </Form.Item>
                        <Form.Item name={['user', 'age']} label="Age" rules={[{ type: 'number', min: 0, max: 99 }]}>
                            <InputNumber />
                        </Form.Item>
                        <Form.Item name={['user', 'github']} label="Github">
                            <Input />
                        </Form.Item>
                        <Form.Item name={['user', 'introduction']} label="Introduction">
                            <Input.TextArea />
                        </Form.Item>
                        <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 8 }}>
                            <Button type="primary" htmlType="submit">
                            Submit
                            </Button>
                        </Form.Item>
                    </Form> :
                <Result />}
            </Card>
            </div>
        </>
    );
};

export default Registration;
